def right():
    direction = loadtxt('direction.csv', delimiter=',')
    if direction <3:
        direction = direction+1
    else:
        direction = 0
    direction = [direction]
    savetxt('direction.csv', direction, delimiter=',')

def left():
    direction = loadtxt('direction.csv', delimiter=',')
    if direction >0:
        direction = direction-1
    else:
        direction = 3
    direction = [direction]
    savetxt('direction.csv', direction, delimiter=',')

def forwards():
    data = loadtxt('data.csv', delimiter=',')
    position = loadtxt('position.csv', delimiter=',')
    direction = loadtxt('direction.csv', delimiter=',')
    conti = loadtxt('conti.csv', delimiter=',')
    if conti == 1 or conti == 2:
        x = check_forwards()
        if x == "space":
            conti = 1
        elif x == "wall":
            conti = 0
        elif x == "goal":
            conti = 2
        data[int(position[-1][0]),int(position[-1][1])] = 2
        if direction == 1:
            newpos = asarray([[int(position[-1][0]),int(position[-1][1]+1)]])
        if direction == 2:
            newpos = asarray([[int(position[-1][0]+1),int(position[-1][1])]])
        if direction == 3:
            newpos = asarray([[int(position[-1][0]),int(position[-1][1]-1)]])
        if direction == 0:
            newpos = asarray([[int(position[-1][0]-1),int(position[-1][1])]])
        position = concatenate((position, newpos))
        data[int(position[-1][0]),int(position[-1][1])] = 3
    if conti == 2:
        conti = 3
    savetxt('data.csv', data, delimiter=',')
    savetxt('position.csv', position, delimiter=',')
    direction = [direction]
    savetxt('direction.csv', direction, delimiter=',')
    conti = [conti]
    savetxt('conti.csv', conti, delimiter=',')

def check_left():
    data = loadtxt('data.csv', delimiter=',')
    position = loadtxt('position.csv', delimiter=',')
    direction = loadtxt('direction.csv', delimiter=',')
    conti = loadtxt('conti.csv', delimiter=',')
    x = 0
    if direction == 2:
            x = data[int(position[-1][0]),int(position[-1][1]+1)]
    if direction == 3:
            x = data[int(position[-1][0]+1),int(position[-1][1])]
    if direction == 0:
            x = data[int(position[-1][0]),int(position[-1][1]-1)]
    if direction == 1:
            x = data[int(position[-1][0]-1),int(position[-1][1])]
    if x == 1:
        return "wall"
    elif x == 0 or x == 2:
        return "space"
    elif x == 4:
        return "goal"
    
