def returnAsText(series):
    asText = ''
    asText = series.str.cat(sep=' ')
    return asText


def returnAsList(series):
    # print('processing for list...')
    list = []
    list = series.to_list()
    return list


def returnAsTextWithNS(series):
    asTextWithNS = ''
    asTextWithNS = series.str.cat(sep='.NS ')
    return asTextWithNS+'.NS'


def returnAsListWithNS(series):
    # suf_res = [sub + append_str for sub in test_list]
    asListWithNS = []
    asListWithNS = [sub + '.NS' for sub in series]
    return asListWithNS


def process_return_type(series, rt):
    if rt == 'as_text':
        return returnAsText(series)
    elif rt == 'as_list':
        return returnAsList(series)
    elif rt == 'as_text_with_NS':
        return returnAsTextWithNS(series)
    elif rt == 'as_list_with_NS':
        return returnAsListWithNS(series)
    else:
        # print('Invalid Return type specified!! Default Output being returned!!')
        return returnAsTextWithNS(series)

